mapper.select:
select NTT_ID,BBS_ID,NTT_NO,NTT_SJ,MIMAGE,NTT_CN,ANSWER_AT,PARNTSCTT_NO,ANSWER_LC,SORT_ORDR,USE_AT,RDCNT,NTCE_BGNDE,NTCE_ENDDE,NTCR_ID,NTCR_NM,PASSWORD,ATCH_FILE_ID,FRST_REGIST_PNTTM,FRST_REGISTER_ID,LAST_UPDT_PNTTM,LAST_UPDUSR_ID,POSITION,TITLE,PTB,FTYPE,TERM,to_char(PUSHDATE,'yyyy-mm-dd') as PUSHDATE,THUMBNAIL,ATTACHMENT,CONTENT,VIDEO,HEAD_SIGN,DECODE(ONOFF , 'Y' , '승인' , 'N', '거절') as ONOFF,MBER_ID,APPROVAL,BOOK_DATE,PRICE,CNT,to_char(FDATE,'yyyy-mm-dd')as FDATE,to_char(SDATE,'yyyy-mm-dd')as SDATE,FDAY,SDAY,FTIME,STIME,COMPANIES,EMAIL_AGREE,TEAMNAME,CRNUM,TEAMCONTENT,ADDRESS,EVENTNAME,TARGET,ENTRYFEE,ITEM,AFFILIATION,TEL_SEC,FOBJECT,STARTING,REQUEST,REQURIEMENT,BUS,NAME,SIMPLECONTENT,PLACE,IMAGE,DETAIL,FFILE,RUNTIME,FYEAR,CAPTAINNAME,FORM,MAKEYEAR,FLOOR,ROOM,MOVEDATE,MOVEPLAN,ITEM_OBJECT,ITEM_SUBJECT,CHARGE_TEL,OFFICE_TEL,EMAIL,HOMEPAGE,LOGO,ITEM_IMAGE,INTRO , '1' AS index_num from COMTNBBS
WHERE NTT_ID = #{NTT_ID}
AND ONOFF = #{ONOFF}
ORDER BY PUSHDATE DESC

mapper.update:
update COMTNGNRLMBER
set PASSWORD = #{password},
MBTLNUM = #{tel},
MBER_EMAIL_ADRES = #{email}
where MBER_ID = #{id}

update COMTNBBS
set CNT = #{CNT}
where BBS_ID = #{BBS_ID}

mapper.select:
select 0 AS RNUM , '공지' AS index_num , NTT_ID,BBS_ID,NTT_NO,NTT_SJ,MIMAGE,NTT_CN,ANSWER_AT,PARNTSCTT_NO,ANSWER_LC,SORT_ORDR,USE_AT,RDCNT,NTCE_BGNDE,NTCE_ENDDE,NTCR_ID,NTCR_NM,PASSWORD,ATCH_FILE_ID,FRST_REGIST_PNTTM,FRST_REGISTER_ID,LAST_UPDT_PNTTM,LAST_UPDUSR_ID,POSITION,TITLE,PTB,FTYPE,TERM,to_char(PUSHDATE,'yyyy-mm-dd') as PUSHDATE,THUMBNAIL,ATTACHMENT,CONTENT,VIDEO,HEAD_SIGN,DECODE(ONOFF , 'Y' , '승인' , 'N', '거절') as ONOFF,MBER_ID,APPROVAL,BOOK_DATE,PRICE,CNT,to_char(FDATE,'yyyy-mm-dd')as FDATE,to_char(SDATE,'yyyy-mm-dd')as SDATE,FDAY,SDAY,FTIME,STIME,COMPANIES,EMAIL_AGREE,TEAMNAME,CRNUM,TEAMCONTENT,ADDRESS,EVENTNAME,TARGET,ENTRYFEE,ITEM,AFFILIATION,TEL_SEC,FOBJECT,STARTING,REQUEST,REQURIEMENT,BUS,NAME,SIMPLECONTENT,PLACE,IMAGE,DETAIL,FFILE,RUNTIME,FYEAR,CAPTAINNAME,FORM,MAKEYEAR,FLOOR,ROOM,MOVEDATE,MOVEPLAN,ITEM_OBJECT,ITEM_SUBJECT,CHARGE_TEL,OFFICE_TEL,EMAIL,HOMEPAGE,LOGO,ITEM_IMAGE,INTRO FROM ( select BBS.* from COMTNBBS BBS where HEAD_SIGN = 'Y'
AND NTT_ID = #{NTT_ID}
order by BBS.FRST_REGIST_PNTTM desc )
union all
SELECT X.RNUM , to_char(index_num) AS index_num , NTT_ID,BBS_ID,NTT_NO,NTT_SJ,MIMAGE,NTT_CN,ANSWER_AT,PARNTSCTT_NO,ANSWER_LC,SORT_ORDR,USE_AT,RDCNT,NTCE_BGNDE,NTCE_ENDDE,NTCR_ID,NTCR_NM,PASSWORD,ATCH_FILE_ID,FRST_REGIST_PNTTM,FRST_REGISTER_ID,LAST_UPDT_PNTTM,LAST_UPDUSR_ID,POSITION,TITLE,PTB,FTYPE,TERM,to_char(PUSHDATE,'yyyy-mm-dd') as PUSHDATE,THUMBNAIL,ATTACHMENT,CONTENT,VIDEO,HEAD_SIGN,DECODE(ONOFF , 'Y' , '승인' , 'N', '거절') as ONOFF,MBER_ID,APPROVAL,BOOK_DATE,PRICE,CNT,to_char(FDATE,'yyyy-mm-dd')as FDATE,to_char(SDATE,'yyyy-mm-dd')as SDATE,FDAY,SDAY,FTIME,STIME,COMPANIES,EMAIL_AGREE,TEAMNAME,CRNUM,TEAMCONTENT,ADDRESS,EVENTNAME,TARGET,ENTRYFEE,ITEM,AFFILIATION,TEL_SEC,FOBJECT,STARTING,REQUEST,REQURIEMENT,BUS,NAME,SIMPLECONTENT,PLACE,IMAGE,DETAIL,FFILE,RUNTIME,FYEAR,CAPTAINNAME,FORM,MAKEYEAR,FLOOR,ROOM,MOVEDATE,MOVEPLAN,ITEM_OBJECT,ITEM_SUBJECT,CHARGE_TEL,OFFICE_TEL,EMAIL,HOMEPAGE,LOGO,ITEM_IMAGE,INTRO
FROM ( select rownum AS RNUM , A.* FROM ( select BBS.* , row_number() over (order by bbs.FRST_REGIST_PNTTM asc) as index_num from COMTNBBS BBS
AND NTT_ID = #{NTT_ID}
order by bbs.FRST_REGIST_PNTTM desc ) A where rownum <= #{endRow}) X where X.rnum >= #{startRow}

SELECT
COUNT(1) AS DUP
FROM COMTNBBS
WHERE 1=1
AND NTT_ID = 'reservrent'
AND BBS_ID = #{TITLE}
AND
TO_CHAR(FDATE , 'YYYY-mm-dd') = #{FDATE}
AND (#{FTIME} BETWEEN FTIME
AND STIME
OR #{STIME} BETWEEN FTIME AND STIME
OR FTIME BETWEEN #{FTIME} AND #{STIME}
OR STIME BETWEEN #{FTIME} AND #{STIME}
)
